function pred = Predict(params, X, network,gg)%need to reshape all theta matricesarray_of_theta_matrices = cell(1, (network.num_of_layers - 1));start_index = 1;fprintf('creating theta matrices and checking sizes IN PREDICT...\n');for i = 1:network.num_of_layers - 1,  end_index = (start_index - 1) + (network.layer_dimen(i + 1) * (network.layer_dimen(i) + 1)); % = start_index + num_of_elements_in_theta_matrix  array_of_theta_matrices(i) = reshape(params(start_index:end_index), network.layer_dimen(i + 1), network.layer_dimen(i) + 1);  fprintf("size of theta(%d): ", i); %PRINT  size(reshape(params(start_index:end_index), network.layer_dimen(i + 1), network.layer_dimen(i) + 1)) %PRINT  start_index = end_index + 1;endm = size(X, 1);pred = zeros(size(X, 1), 1);for i = 1:network.num_of_layers - 1,  theta = cell2mat(array_of_theta_matrices(i));  if(i == 1)    hyp = SigmoidAct([ones(m,1) X] * theta');  else     hyp = SigmoidAct([ones(m,1) temp] * theta');   endif   temp = hyp;end%h1 = sigmoid([ones(m, 1) X] * Theta1');%h2 = sigmoid([ones(m, 1) h1] * Theta2');fprintf('size of pred')size(pred)[dummy, pred] = max(hyp, [], 2); %this line of code finds the maximum value in every row of matrix tempend